\define get-tc-classes()
[get[text]split[{]split[}]split[/*]split[*/]split[,]split[>]split[:]search-replace:g[.tc],[ .tc]split[ ]search[.tc-]!search[<<]!search[>>]!search[""]unique[]]
\end
\define other-classes()
<$text text={{{
[{$:/theme}plugintiddlers[]tag[$:/tags/Stylesheet]subfilter<get-tc-classes>] 
:filter[!search:title:some{$(currentTiddler)$!!categories}]
+[join[, ]]
}}}/>
\end

Number of classes in $:/themes/tiddlywiki/vanilla/base : {{{ [[$:/themes/tiddlywiki/vanilla/base]subfilter<get-tc-classes>count[]] }}}

<table class="tool-categories-finder">
<thead>
<th>Categories</th><th>Other classes</th>
</thead>
<tr>
<td>
<$edit-text field="categories" tag="textarea" class="full"/>
</td>
<td>
<<other-classes>>
</td>
</tr>
</table>

<style>
.tool-categories-finder{
table-layout:fixed;
width:100%;
}

.tool-categories-finder td{
position:relative;
}
.tool-categories-finder .full{
position:absolute;
inset:0;
width:100%;
height:100%!important;
}
</style>
